import axios from 'axios';

const axiosInstance = axios.create({
    baseURL: 'http://localhost:5074', // כתובת ה-API שלך
});

// תפס שגיאות ב-response
axiosInstance.interceptors.response.use(
    response => response,
    error => {
        console.error('Error occurred:', error); // רישום ללוג
        return Promise.reject(error);
    }
);

export default {
  getTasks: async () => {
    const result = await axios.get('')    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    const result = await axios.post('', {name,isComplete:false})
    return {};
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    const result = await axios.put(`/${id}?iscomplete=${isComplete}`)
    return {};
  },

  deleteTask:async(id)=>{
    const result = await axios.delete(`/${id}`)
    console.log('deleteTask')
  }
};
